import React, { useState } from 'react';
import { auth } from '../firebaseConfig'; // Make sure this path is correct
import {
  signInWithEmailAndPassword,
  createUserWithEmailAndPassword,
  signOut,
} from 'firebase/auth';

const LoginRegister = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isRegistering, setIsRegistering] = useState(false);
  const [user, setUser] = useState(null);

  const handleAuth = async (e) => {
    e.preventDefault();
    try {
      let userCredential;
      if (isRegistering) {
        userCredential = await createUserWithEmailAndPassword(auth, email, password);
        alert('Registration successful!');
      } else {
        userCredential = await signInWithEmailAndPassword(auth, email, password);
        alert('Login successful!');
      }
      setUser(userCredential.user);
    } catch (error) {
      alert(error.message);
      console.error(error.code, error.message);
    }
  };

  const handleLogout = async () => {
    await signOut(auth);
    setUser(null);
  };

  return (
    <div style={{ maxWidth: 400, margin: '2rem auto', padding: '1rem', border: '1px solid #ccc', borderRadius: '8px' }}>
      {user ? (
        <>
          <h3>Welcome, {user.email}</h3>
          <button onClick={handleLogout}>Logout</button>
        </>
      ) : (
        <>
          <h2>{isRegistering ? 'Register' : 'Login'}</h2>
          <form onSubmit={handleAuth}>
            <input
              type="email"
              placeholder="Email"
              required
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              style={{ display: 'block', margin: '10px 0', width: '100%', padding: '8px' }}
            />
            <input
              type="password"
              placeholder="Password (min 6 chars)"
              required
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={{ display: 'block', margin: '10px 0', width: '100%', padding: '8px' }}
            />
            <button type="submit" style={{ padding: '8px 16px', marginBottom: '10px' }}>
              {isRegistering ? 'Register' : 'Login'}
            </button>
          </form>
          <p>
            {isRegistering ? 'Already have an account?' : "Don't have an account?"}{' '}
            <button onClick={() => setIsRegistering(!isRegistering)} style={{ marginLeft: 4 }}>
              {isRegistering ? 'Login here' : 'Register here'}
            </button>
          </p>
        </>
      )}
    </div>
  );
};

export default LoginRegister;
