import React, { useEffect, useState } from 'react';
import axios from './mockApi'; // Make sure you use the mocked axios
import { onAuthStateChanged } from 'firebase/auth';
import { auth } from './firebaseConfig';

import StudentList from './components/StudentList';
import AddStudentForm from './components/AddStudentForm';
import LoginForm from './components/LoginForm';

import './App.css';

function App() {
  const [students, setStudents] = useState([]);
  const [user, setUser] = useState(null);
  const [filter, setFilter] = useState('');
  const [showAddForm, setShowAddForm] = useState(false);

  // Fetch students on mount
  useEffect(() => {
    axios.get('/students').then((res) => setStudents(res.data));
  }, []);

  // Track login state
  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
    });
    return () => unsubscribe();
  }, []);

  // Filter logic
  const filteredStudents = students.filter((student) =>
    student.course.toLowerCase().includes(filter.toLowerCase())
  );

  return (
    <div className="container">
      <h1>Student Management</h1>

      <input
        type="text"
        placeholder="Filter by course"
        value={filter}
        onChange={(e) => setFilter(e.target.value)}
        className="filter-input"
      />

      {/* Always show student list */}
      <StudentList students={filteredStudents} />

      {/* Button to add student */}
      <button onClick={() => setShowAddForm(true)} className="add-button">
        Add New Student
      </button>

      {/* Conditional: Add Form or Login */}
      {showAddForm && (
        user ? (
          <AddStudentForm setStudents={setStudents} />
        ) : (
          <>
            <p>You must log in to add a new student.</p>
            <LoginForm />
          </>
        )
      )}
    </div>
  );
}

export default App;
